export const ActionGetMovies = (data) => {
    return {
        type: 'GET_MOVIES',
        payload: data
    }
    
}

export const ActionAddMovie = (id, name) => {
    return {
        type: 'ADD_MOVIE',
        payload: {id, name}
    }
    
}

export const ActionDelMovie = (id) => {
    return {
        type: 'DEL_MOVIE',
        payload: id
    }
    
}

const localState = [  
    {id: 1, name: "La Ligne Verte"} ,
    {id: 2, name: "Independance Day"} ,
    {id: 3, name: "Star Wars"} ,
    {id: 4, name: "Bienvenue à Gattaga"} 
]

export const moviesReducer = (state = localState, action) => {
        
        switch (action.type) 
        {
            case 'GET_MOVIES' :
                return state;
            case 'ADD_MOVIE' :
                return [...state, action.payload];
            case 'DEL_MOVIE' :
                return state.filter(movie => movie.id !== action.payload);
            default :
                return state;
        }
}
