import React, { useState, useContext } from "react";
import { Container, ListGroup, ListGroupItem, Button } from "reactstrap";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { GlobalContext } from "../context/GlobalContext";
import DeleteServices from "../actions/DeleteServices";

const ServicesList = props => {
	const [services, setServices, movies, setMovies] = useContext(GlobalContext);

	return (
		<Container>
			<ListGroup>
				<TransitionGroup className="services-list">
					{services.map(({ id, name }) => (
						<CSSTransition key={id} timeout={500} classNames="fade">
							<ListGroupItem className="list">
								{name}
								<DeleteServices></DeleteServices>
							</ListGroupItem>
						</CSSTransition>
					))}
				</TransitionGroup>
			</ListGroup>
			<ListGroup>
				<TransitionGroup className="services-list">
					{movies.map(({ id, name }) => (
						<CSSTransition key={id} timeout={500} classNames="fade">
							<ListGroupItem className="list">
								{name}
								<Button
									className="remove-btn"
									color="danger"
									size="sm"
									onClick={() => {
										setMovies(movies.filter(service => movies.id !== id));
									}}>
									X
								</Button>
							</ListGroupItem>
						</CSSTransition>
					))}
				</TransitionGroup>
			</ListGroup>
		</Container>
	);
};

export default ServicesList;
