import React from "react";
import { Button } from "reactstrap";
import {ActionAddMovie} from '../reducers/addMovies';
import {useDispatch} from 'react-redux';

const AddMovies = () => {

	const dispatch = useDispatch();

	return (
		<Button
			color="dark"
			style={{
				marginBottom: "2rem"
			}}
			onClick={() => {
				const namePrompt = prompt("Enter what U want");
				if (namePrompt) {
					dispatch(ActionAddMovie(28, namePrompt))
				}
			}}>
			Ajouter un Film
		</Button>
	);
};

export default AddMovies;
