import React, { useState, createContext } from "react"

export const GlobalContext = createContext()

export const DataContext = props => {
	const [services, setServices] = useState(
		{services:[
			//CREATION
			{ id: 1, name: "Site vitrine" },
			{ id: 2, name: "Site e-commerce" },
			{ id: 3, name: "Site de publication" },
			{ id: 4, name: "Application mobile native" },
			//EXISTANT
			{
				id: 5,
				name: "Modernisation de site existants (technique et/ou visuelle)"
			},
			{ id: 6, name: "Site responsive toute taille d'écran " },
			{ id: 7, name: "Créer une Application de mon site existant" },
			//ENTREPRISE
			{ id: 8, name: "Besoin d'un développeur ?" } ],

		loading:false
	}
)



	return (
		<GlobalContext.Provider value={[services, setServices]}>
			{props.children}
		</GlobalContext.Provider>
	)
}
