import React, {useState} from 'react';
import {Container, ListGroup, ListGroupItem, Button} from 'reactstrap';
import {CSSTransition, TransitionGroup} from 'react-transition-group'

const ServicesList = props => {
    const [services, setServices] = useState( [
        //CREATION
        {id:1, name:'Site vitrine'},
        {id:2, name:'Site e-commerce'},
        {id:3, name:'Site de publication'},
        {id:4, name:'Application mobile native'},
        //EXISTANT
        {id:5, name:'Modernisation de site existants (technique et/ou visuelle)'},
        {id:6, name:'Site responsive toute taille d\'écran '},
        {id:7, name:'Créer une Application de mon site existant'},
        //ENTREPRISE
        {id:8, name:'Besoin d\'un développeur ?'},
    ])

    return (
        <Container>
            <Button 
                color="dark"
                style={{marginBottom: '2rem'}}
                onClick={() => {
                    const namePrompt = prompt('Enter what U want')
                    if(namePrompt) {
                        setServices([...services, { id: Math.floor(Math.random() * (20000 - 9 + 1)) + 9, name:namePrompt }])
                    }
                }}>
                Ajouter
            </Button>
        </Container>
    )
}

export default ServicesList;
