export const ActionAddMovie = (id, name) => {
    return {
        type: 'ADD_MOVIE',
        payload: {id, name}
    }
    
}

export const ActionDelMovie = (id) => {
    return {
        type: 'DEL_MOVIE',
        payload: id
    }
    
}

export const moviesReducer = (

    state=[  {id: 1, name: "La Ligne Verte"} ,
             {id: 2, name: "Independance Day"} ,
             {id: 3, name: "Star Wars"} ,
             {id: 4, name: "Bienvenue à Gattaga"} ],
    action) => {
        
        switch (action.type) 
        {
            case 'ADD_MOVIE' :
                return [...state, action.payload];
                
            case 'DEL_MOVIE' :
                return state.filter(movie => movie.id !== action.payload);
            default :
                return state;
        }
}
