import React, { useContext } from "react";
import { Container, ListGroup, ListGroupItem, Button } from "reactstrap";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import AddServices from "./AddServicesButton";
import AddMovies from "./AddMoviesButton";
import { GlobalContext } from "../context/GlobalContext";
import {useDispatch, useSelector} from 'react-redux';
import {ActionDelMovie} from '../reducers/addMovies';
import axios from 'axios';


const ServicesList = props => {
	const [services, setServices] = useContext(GlobalContext);
	const movies = useSelector(state => state.handleMovies)
	const dispatch = useDispatch();

	const delService = (id) => axios({
		method: 'post',
		url: `http://localhost:5000/posts/${id}`
		})

	return (

	<div>
		<Container style={{marginBottom: '2rem'}}>
		<AddServices></AddServices>
			<ListGroup>
				<TransitionGroup className="services-list">
					{services.map(({ _id, title, desc }) => (
						<CSSTransition key={_id} timeout={500} classNames="fade">
							<ListGroupItem className="list">
								{desc}
								<Button
									className={'remove-btn'+_id}
									color="danger"
									size="sm"
									onClick={() => {
										delService(_id);
										setServices(services.filter(service => service._id !== _id))
									}}>
									X
								</Button>
							</ListGroupItem>
						</CSSTransition>
					))}
				</TransitionGroup>
			</ListGroup>
		</Container>
		
		<Container>
			<AddMovies></AddMovies>
			<ListGroup>
				<TransitionGroup className="movies-list">
					{movies.map(({ id, name }) => (
						<CSSTransition key={id} timeout={500} classNames="fade">
							<ListGroupItem className="list">
								{name}
								<Button
									className={'remove-btn'+id}
									color="danger"
									size="sm"
									onClick={() => {
										dispatch(ActionDelMovie(id))
									}}>
									X
								</Button>
							</ListGroupItem>
						</CSSTransition>
					))}
				</TransitionGroup>
			</ListGroup>
		</Container>
	</div>
	);
};

export default ServicesList;
