import React, { useState, useContext } from "react";
import { Container, ListGroup, ListGroupItem, Button } from "reactstrap";
import { CSSTransition, TransitionGroup } from "react-transition-group";
import { GlobalContext } from "../context/GlobalContext";
import {useDispatch, useSelector} from 'react-redux';
import {ActionDelMovie} from '../reducers/addMovies';


const ServicesList = props => {
	const [services, setServices] = useContext(GlobalContext);
	const movies = useSelector(state => state)
	const dispatch = useDispatch();

	return (

	<div>
		<Container>
			<ListGroup>
				<TransitionGroup className="services-list">
					{services.map(({ id, name }) => (
						<CSSTransition key={id} timeout={500} classNames="fade">
							<ListGroupItem className="list">
								{name}
								<Button
									className={'remove-btn'+id}
									color="danger"
									size="sm">
									X
								</Button>
							</ListGroupItem>
						</CSSTransition>
					))}
				</TransitionGroup>
			</ListGroup>
		</Container>
		
		<Container>
			<ListGroup>
				<TransitionGroup className="movies-list">
					{movies.map(({ id, name }) => (
						<CSSTransition key={id} timeout={500} classNames="fade">
							<ListGroupItem className="list">
								{name}
								<Button
									className={'remove-btn'+id}
									color="danger"
									size="sm"
									onClick={() => {
										dispatch(ActionDelMovie(id))
									}}>
									X
								</Button>
							</ListGroupItem>
						</CSSTransition>
					))}
				</TransitionGroup>
			</ListGroup>
		</Container>
	</div>
	);
};

export default ServicesList;
