export const ActionAddGame = (id, name) => {
    return {
        type: 'ADD_GAME',
        payload: {id, name}
    }
    
}

export const ActionDelGame = (id, name) => {
    return {
        type: 'DEL_GAME',
        payload: {id, name}
    }
    
}

export const gamesReducer = (

    state=[
            { id: 1, name: "Halo" },
            { id: 2, name: "Metal Gear" },
            { id: 3, name: "Zelda" },
            { id: 4, name: "Mario" }
        ], 
    action) => {
        
        switch (action.type) 
        {
            case 'ADD_GAME' :
                return [...state, action.payload.id, action.payload.name]

            case 'DEL_GAME' :
                return state.filter(movie => movie.id !== action.payload.id)

            default :
             return state;
                
        }
}
