export const ActionGetMovies = (data) => {
    return {
        type: 'GET_MOVIES',
        payload: data
    }
    
}

export const ActionAddMovie = (id, name) => {
    return {
        type: 'ADD_MOVIE',
        payload: {id, name}
    }
    
}

export const ActionDelMovie = (id) => {
    return {
        type: 'DEL_MOVIE',
        payload: id
    }
    
}

export const moviesReducer = (state = [], action) => {
        
        switch (action.type) 
        {
            case 'GET_MOVIES' :
                return action.payload;
            case 'ADD_MOVIE' :
                return [...state, action.payload];
            case 'DEL_MOVIE' :
                return state.filter(movie => movie.id !== action.payload);
            default :
                return state;
        }
}
