import React, { useContext } from "react";
import { Button } from "reactstrap";
import { GlobalContext } from "../context/GlobalContext";

const AddMovie = () => {
	const [movies, setMovies] = useContext(GlobalContext);

	return (
		<Button
			color="dark"
			style={{
				marginBottom: "2rem"
			}}
			onClick={() => {
				const namePrompt = prompt("Enter what U want");
				if (namePrompt) {
					setMovies([
						...movies,
						{
							id: Math.floor(Math.random() * (20000 - 9 + 1)) + 9,
							name: namePrompt
						}
					]);
				}
			}}>
			Ajouter
		</Button>
	);
};

export default AddMovie;
